import React from 'react';
// import React, { useContext } from 'react';
import QRCode from 'qrcode.react';
import { ShopContext } from '../../Context/ShopContext';
import qrcode from '../assets/scanner.png';

const PaymentPage = () => {
    const { getTotalCartAmount } = useContext(ShopContext);
    const amount = getTotalCartAmount();
    console.log('Total Cart Amount:', amount); // Add this line

    // Convert QR code to image
    const qrCodeDataURL = QRCode.toDataURL(`Amount: ₹${amount}`);

    return (
        <div className="payment-page">
            <h2>Scan the QR code to proceed with payment</h2>
            <div className="qr-code-container">
                <img src={qrcode} alt="QR Code" />
            </div>
            <p>Amount: ₹{amount}</p>
            <p>Please scan the QR code using your payment app to complete the transaction.</p>
        </div>
    );
};

export default PaymentPage;
