import React from 'react';
import Slider from 'react-slick';
import './Hero.css';
import hand_icon from '../assets/hand_icon.png';
import arrow_icon from '../assets/arrow.png';
import hero_img1 from '../assets/product_18.png'; // Image 1
import hero_img2 from '../assets/product_25.png'; // Image 2
import hero_img3 from '../assets/product_4.png'; // Image 3
import hero_img4 from '../assets/product_1.png'; // Image 4

const Hero = () => {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 2000, // Adjust autoplay speed as needed
    };
  
    return (
      <div className="hero">
        <Slider {...settings}>
          {/* Slide 1 */}
          <div className="hero-slide">
            <img src={hero_img1} alt="" />
          </div>
  
          {/* Slide 2 */}
          <div className="hero-slide">
            <img src={hero_img2} alt="" />
          </div>
  
          {/* Slide 3 */}
          <div className="hero-slide">
            <img src={hero_img3} alt="" />
          </div>
  
          {/* Slide 4 */}
          <div className="hero-slide">
            <img src={hero_img4} alt="" />
          </div>
        </Slider>
      </div>
    );
  };
  
  export default Hero;
