import './App.css';
import Navbar from './Components/Navbar/Navbar';
import {BrowserRouter,Routes ,Route} from 'react-router-dom' 
import Shop from './Pages/Shop';
import ShopCategory from './Pages/ShopCategory';
import LoginSignup from './Pages/LoginSignup';
import Product from './Pages/Product';
import Cart from './Pages/Cart';
import Footer from './Components/Footer/Footer';
import men_banner from './Components/assets/banner_mens.png'
import women_banner from  './Components/assets/banner_women.png'
import kid_banner from  './Components/assets/banner_kids.png'
import ProtectedRoute from './ProtectedRoute'; // Assuming the ProtectedRoute component is in the same directory
import React, { useState } from 'react';

function App() {
  const [user, setUser] = useState(null); // Authentication state

  // Function to handle login
  const handleLogin = (userData) => {
      setUser(userData); // Set user data upon successful login
  };

  // Function to handle logout
  const handleLogout = () => {
      setUser(null); // Clear user data upon logout
  };

// Your component structure
    return (
        <BrowserRouter>
            <Navbar />
            <Routes>
                <Route path='/' element={<Shop />} />
                <Route path='/mens' element={<ShopCategory banner={men_banner} category="men" />} />
                <Route path='/womens' element={<ShopCategory banner={women_banner} category="women" />} />
                <Route path='/kids' element={<ShopCategory banner={kid_banner} category="kid" />} />
                <Route path="product" element={<Product />}>
                    <Route path=':productid' element={<Product />} />
                </Route>
                {/* Use ProtectedRoute for the cart */}
                <ProtectedRoute path='/cart' element={<Cart />} user={user} />
                <Route path='/login' element={<LoginSignup onLogin={handleLogin} />} />
            </Routes>
            <Footer />
        </BrowserRouter>
    );
}

export default App;
