import React, { useContext } from 'react'
import './Cartitems.css'
import remove_icon from '../assets/cart_cross_icon.png'
import { ShopContext } from '../../Context/ShopContext'

const Cartitems = () => {
    const { getTotalCartAmount, all_product, cartItems, removeFromCart } = useContext(ShopContext);

    return (
        <div className='cartitems'>
            <div className="cartitems-format-main">
                <p>Products</p>
                <p>Title</p>
                <p>Price</p>
                <p>Quantity</p>
                <p>Total</p>
                <p>Remove</p>
            </div>
            <hr />
            {all_product.map((e) => {
                if (cartItems[e.id] > 0) {
                    return (
                        <div key={e.id}>
                            <div className="cartitems-format cartitems-format-main">
                                <img className='carticon-product-icon' src={e.image} alt="" />
                                <p>{e.name}</p>
                                <p>â‚¹{e.new_price}</p> {/* Replaced $ with â‚¹ */}
                                <button className='cartitems-quantity'>{cartItems[e.id]}</button>
                                <p>â‚¹{e.new_price * cartItems[e.id]}</p> {/* Replaced $ with â‚¹ */}
                                <img className='carticon-remove-icon' src={remove_icon} onClick={() => { removeFromCart(e.id) }} alt="" />
                            </div>
                            <hr />
                        </div>
                    );
                }
                return null;
            })}
            <div className="cartitems-down">
                <div className="cartitems-total">
                    <h1>Cart Totals</h1>
                    <div>
                        <div className="cartitems-total-item">
                            <p>Subtotal</p>
                            <p>â‚¹{getTotalCartAmount()}</p> {/* Replaced $ with â‚¹ */}
                        </div>
                        <hr />
                        <div className="cartitems-total-item">
                            <p>Shipping Fee</p>
                            <p>Free</p>
                        </div>
                        <hr />
                        <div className="cartitems-total-item">
                            <h3>Total</h3>
                            <h3>â‚¹{getTotalCartAmount()}</h3> {/* Replaced $ with â‚¹ */}
                        </div>
                    </div>
                    <button>PROCEED TO CHECKOUT</button>
                </div>
            </div>
        </div>
    )
}

export default Cartitems;
